import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; // Import FontAwesome
import { faEdit, faTrash, faCheckCircle } from '@fortawesome/free-solid-svg-icons'; // Import specific icons
import { Todo } from '../../model';

interface Props {
  todos: Todo[];
}

const SingleTodo: React.FC<Props> = ({ todos }) => {
  return (
    <ul>
      {todos
        .filter((item) => item.todo !== "")
        .map((item) => (
          <li key={item.id}>
            {item.todo}
            <span>
              <FontAwesomeIcon icon={faEdit} /> {/* Edit icon */}
              <FontAwesomeIcon icon={faTrash} /> {/* Delete icon */}
              <FontAwesomeIcon icon={faCheckCircle} /> {/* Complete icon */}
            </span>
          </li>
        ))}
    </ul>
  );
}

export default SingleTodo;
