import React, { useState } from 'react';
import './InputField.scss';

const InputField = () => {
  const [isFocused, setIsFocused] = useState(false);

  const handleFocus = () => {
    setIsFocused(true);
  };

  const handleBlur = () => {
    setIsFocused(false);
  };

  return (
    <form className={`input ${isFocused ? 'focused' : ''}`}>
      <input
        type="input"
        placeholder="Enter a task"
        className={`input-box ${isFocused ? 'focused' : ''}`}
        onFocus={handleFocus}
        onBlur={handleBlur}
      />
      <button type="submit" className={`input-submit ${isFocused ? 'focused' : ''}`}>
        Go
      </button>
    </form>
  );
};

export default InputField;
