import React from 'react';
import { Todo } from '../../model';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faEdit, faTrash, faCheckCircle } from '@fortawesome/free-solid-svg-icons';

import './SingleTodo.scss'; // Import your Sass stylesheet

library.add(faEdit, faTrash, faCheckCircle);

interface Props {
  todos: Todo[];
}
const handleDone = (id:number) => {
    console.log("todo ", id);
    const updatedTodos = [...todos];

}
const handleToggleComplete = (index) => {
    const updatedTodos = [...todos];
    updatedTodos[index].completed = !updatedTodos[index].completed;
    getUpdatedTodos(updatedTodos);
  };

const SingleTodo: React.FC<Props> = ({ todos }) => {
  return (
    <ul>
      {todos
        .filter((item) => item.todo !== "")
        .map((item) => (
          <li key={item.id}>
            {item.todo}
            <span>
              <FontAwesomeIcon icon={faEdit} /> {/* Edit icon */}
              <FontAwesomeIcon icon={faTrash} /> {/* Delete icon */}
              <FontAwesomeIcon icon={faCheckCircle} onClick={()=>handleDone(item.id)}/> {/* Complete icon */}
            </span>
          </li>
        ))}
    </ul>
  );
}

export default SingleTodo;
