// SingleTodo.tsx
import React from 'react';
import { Todo } from '../../model';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash, faCheckCircle } from '@fortawesome/free-solid-svg-icons';

import './SingleTodo.scss';

interface Props {
  todos: Todo[];
  settodos: React.Dispatch<React.SetStateAction<Todo[]>>;
}

const SingleTodo: React.FC<Props> = ({ todos, settodos }) => {
  const handleDone = (id: number) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return { ...todo, isDone: !todo.isDone };
      }
      return todo;
    });
    settodos(updatedTodos);
  };

  return (
    <ul>
      {todos
        .filter((item) => item.todo !== "")
        .map((item) => (
          <li key={item.id}>
            {item.todo}
            <span>
              <FontAwesomeIcon icon={faEdit} />
              <FontAwesomeIcon icon={faTrash} />
              <FontAwesomeIcon
                icon={faCheckCircle}
                onClick={() => handleDone(item.id)} // Call handleDone with the item's ID
              />
            </span>
          </li>
        ))}
    </ul>
  );
};

export default SingleTodo;
