import React from 'react';
import { Todo } from '../../model';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faEdit, faTrash, faCheckCircle } from '@fortawesome/free-solid-svg-icons';
import './SingleTodo.scss';
library.add(faEdit, faTrash, faCheckCircle);

type Props = {
//   todos: Todo[];
  item: Todo;
  todos: Todo[],
  settodos:  React.Dispatch<React.SetStateAction<Todo[]>>
}

const SingleTodo: React.FC<Props> = ({item, todos, settodos}) => {
  const handleComplete = (id:number) => {
    const updatedTodos = [...todos];
    updatedTodos.map((todo) => {
        todo.id === id ? {...todo, isDone: !isDone} : todo

        updatedTodos[id].isDone = !updatedTodos[id].isDone;
        settodos(updatedTodos)
        console.log(item.todo , "id is ", item.id)   
        console.log(item.todo , "isDone value is ", item.isDone)         
       
  })
}
 
  return (
    <form className="SingleTodoContainer">
        <li key={item.id}>
            {item.todo}
            <span>
              <FontAwesomeIcon icon={faEdit} /> {/* Edit icon */}
              <FontAwesomeIcon icon={faTrash} /> {/* Delete icon */}
              <FontAwesomeIcon icon={faCheckCircle} onClick={()=>handleComplete(item.id)}/> {/* Complete icon */}
            </span>
        </li>
    </form>  
  );
}

export default SingleTodo;
